package com.taotao.cloud.cache.listener;

import com.taotao.cloud.cache.api.CacheRemoveListener;
import com.taotao.cloud.cache.api.CacheRemoveListenerContext;

/**
 * @author shuigedeng
 * @since 2024.06
 */
public class MyRemoveListener<K, V> implements CacheRemoveListener<K, V> {

    @Override
    public void listen( CacheRemoveListenerContext<K, V> context) {
        System.out.println("【删除提示】可恶，我竟然被删除了！" + context.key());
    }
}
